import React, { useState, useEffect } from 'react';
import './App.css';
import { getUsers, createUser } from './services/api.js';

function App() {
    const [users, setUsers] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        income: '',
        creditScore: '',
        employmentStatus: '',
        loanAmount: '',
        loanTerm: ''
    });
    const [showSimulation, setShowSimulation] = useState(false);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const usersData = await getUsers();
            setUsers(usersData.reverse());
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createUser(formData);
            fetchUsers();
            setShowSimulation(true);
        } catch (error) {
            console.error('Error creating user:', error);
        }
    };

    const getCreditScoreColor = (score) => {
        if (score >= 0 && score <= 300) return 'red';
        if (score >= 350 && score <= 600) return 'orange';
        if (score >= 650 && score <= 1000) return 'green';
        return 'black';
    };

    return (
        <div className="App">
            <h1>Banking Services Platform</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
                <input type="number" name="income" value={formData.income} onChange={handleChange} placeholder="Income" required />
                <select name="creditScore" value={formData.creditScore} onChange={handleChange} required>
                    {[...Array(21).keys()].map(i => {
                        const score = i * 50;
                        return <option key={score} value={score} style={{ color: getCreditScoreColor(score) }}>{score}</option>
                    })}
                </select>
                <select name="employmentStatus" value={formData.employmentStatus} onChange={handleChange} required>
                    <option value="Employed">Employed</option>
                    <option value="Unemployed">Unemployed</option>
                </select>
                <input type="number" name="loanAmount" value={formData.loanAmount} onChange={handleChange} placeholder="Loan Amount" required />
                <select name="loanTerm" value={formData.loanTerm} onChange={handleChange} required>
                    {[...Array(40).keys()].map(i => <option key={i + 1} value={i + 1}>{i + 1}</option>)}
                </select>
                <button type="submit">Submit</button>
            </form>
            <h2>Users</h2>
            <ul>
                {users.map((user) => (
                    <li key={user.id}>
                        {user.name}
                    </li>
                ))}
            </ul>
            {showSimulation && (
                <>
                    <h2>Would you like to do a credit simulation next?</h2>
                    <select>
                        {users.map((user) => (
                            <option key={user.id} value={user.id}>{user.name}</option>
                        ))}
                    </select>
                    <button>Simulate</button>
                </>
            )}
        </div>
    );
}

export default App;
