import React, { useState, useEffect } from 'react';
import './App.css';
import { getUsers, createUser, simulateCredit } from './services/api.js';

function App() {
    const [users, setUsers] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        income: '',
        creditScore: 0,
        employmentStatus: 'Employed',
        loanAmount: '',
        loanTerm: 1
    });
    const [simulationData, setSimulationData] = useState({
        name: '',
        income: '',
        creditScore: 0,
        employmentStatus: 'Employed',
        loanAmount: '',
        loanTerm: 1
    });
    const [simulationResult, setSimulationResult] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const usersData = await getUsers();
            setUsers(usersData);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSimulationChange = (e) => {
        const { name, value } = e.target;
        setSimulationData({
            ...simulationData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            await createUser(formData);
            fetchUsers();
        } catch (error) {
            console.error('Error creating user:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSimulationSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const result = await simulateCredit(simulationData);
            setSimulationResult(result);
        } catch (error) {
            console.error('Error simulating credit:', error);
        } finally {
            setLoading(false);
        }
    };

    const getCreditScoreColor = (score) => {
        if (score <= 300) return 'orange';
        if (score <= 600) return 'yellow';
        return 'green';
    };

    return (
        <div className="App">
            <h1>Banking Services Platform</h1>
            <form onSubmit={handleSubmit} className="form">
                <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
                <input type="number" name="income" value={formData.income} onChange={handleChange} placeholder="Income" required />
                <select name="creditScore" value={formData.creditScore} onChange={handleChange} style={{ color: getCreditScoreColor(formData.creditScore) }}>
                    {Array.from({ length: 21 }, (_, i) => i * 50).map(score => (
                        <option key={score} value={score}>{score}</option>
                    ))}
                </select>
                <select name="employmentStatus" value={formData.employmentStatus} onChange={handleChange}>
                    <option value="Employed">Employed</option>
                    <option value="Unemployed">Unemployed</option>
                </select>
                <input type="number" name="loanAmount" value={formData.loanAmount} onChange={handleChange} placeholder="Loan Amount" required />
                <select name="loanTerm" value={formData.loanTerm} onChange={handleChange}>
                    {Array.from({ length: 40 }, (_, i) => i + 1).map(term => (
                        <option key={term} value={term}>{term}</option>
                    ))}
                </select>
                <button type="submit" disabled={loading}>{loading ? 'Submitting...' : 'Submit'}</button>
            </form>

            <h2>Users</h2>
            <ul>
                {users.map((user) => (
                    <li key={user.id}>
                        {user.name}
                    </li>
                ))}
            </ul>

            <h2>Credit Simulation</h2>
            <form onSubmit={handleSimulationSubmit} className="form">
                <select name="name" value={simulationData.name} onChange={handleSimulationChange} required>
                    <option value="">Select User</option>
                    {users.map((user) => (
                        <option key={user.id} value={user.name}>{user.name}</option>
                    ))}
                </select>
                <input type="number" name="income" value={simulationData.income} onChange={handleSimulationChange} placeholder="Income" required />
                <select name="creditScore" value={simulationData.creditScore} onChange={handleSimulationChange} style={{ color: getCreditScoreColor(simulationData.creditScore) }}>
                    {Array.from({ length: 21 }, (_, i) => i * 50).map(score => (
                        <option key={score} value={score}>{score}</option>
                    ))}
                </select>
                <select name="employmentStatus" value={simulationData.employmentStatus} onChange={handleSimulationChange}>
                    <option value="Employed">Employed</option>
                    <option value="Unemployed">Unemployed</option>
                </select>
                <input type="number" name="loanAmount" value={simulationData.loanAmount} onChange={handleSimulationChange} placeholder="Loan Amount" required />
                <select name="loanTerm" value={simulationData.loanTerm} onChange={handleSimulationChange}>
                    {Array.from({ length: 40 }, (_, i) => i + 1).map(term => (
                        <option key={term} value={term}>{term}</option>
                    ))}
                </select>
                <button type="submit" disabled={loading}>{loading ? 'Simulating...' : 'Simulate'}</button>
            </form>

            {simulationResult && (
                <div className="simulation-result">
                    <h3>Result:</h3>
                    <p>Approved: {simulationResult.approved ? 'Yes' : 'No'}</p>
                    <p>Interest Rate: {simulationResult.interestRate}%</p>
                </div>
            )}
        </div>
    );
}

export default App;
